import React from 'react';
import './App.css';

class RecipeApp extends React.Component {
  constructor(props) {
    super(props);
    const storedRecipes = localStorage.getItem('recipes');
    const storedShoppingList = localStorage.getItem('shoppingList');
    const storedOwnedIngredients = localStorage.getItem('ownedIngredients');

    this.state = {
      recipes: storedRecipes
        ? JSON.parse(storedRecipes)
        : {
            Pancakes: ['1 cup Flour', '1 cup Milk', '2 Eggs', '2 tbsp Butter', '1/4 tsp Salt', '1 tbsp Sugar'],
            Salad: ['1 head Lettuce', '2 Tomatoes', '1 Cucumber', '1/2 Onion', '2 tbsp Olive Oil', '1 tbsp Vinegar', 'Salt', 'Pepper'],
          },
      currentRecipe: '',
      shoppingList: storedShoppingList ? JSON.parse(storedShoppingList) : {},
      ownedIngredients: storedOwnedIngredients ? JSON.parse(storedOwnedIngredients) : {},
      newOwnedIngredient: '',
    };
  }

  handleRecipeChange = (event) => {
    this.setState({ currentRecipe: event.target.value });
  };

  handleOwnedIngredientChange = (event) => {
    this.setState({ newOwnedIngredient: event.target.value });
  };

  addOwnedIngredient = () => {
    const { newOwnedIngredient, ownedIngredients } = this.state;

    if (newOwnedIngredient.trim() === '') {
      return;
    }

    ownedIngredients[newOwnedIngredient] = true;

    this.setState({ ownedIngredients, newOwnedIngredient: '' }, () => {
      this.updateShoppingList();
      this.updateLocalStorage();
    });
  };

  updateShoppingList = () => {
    const { recipes, ownedIngredients } = this.state;
    const updatedShoppingList = {};

    Object.values(recipes).forEach((recipeIngredients) => {
      recipeIngredients.forEach((ingredient) => {
        const spaceIndex = ingredient.indexOf(' ');
        let quantity;
        let ingredientName;

        if (spaceIndex === -1) {
          quantity = '1';
          ingredientName = ingredient;
        } else {
          quantity = ingredient.slice(0, spaceIndex);
          ingredientName = ingredient.slice(spaceIndex + 1);
        }

        if (!ownedIngredients[ingredientName]) {
          if (updatedShoppingList[ingredientName]) {
            updatedShoppingList[ingredientName] += parseFloat(quantity);
          } else {
            updatedShoppingList[ingredientName] = parseFloat(quantity);
          }
        }
      });
    });

    this.setState({ shoppingList: updatedShoppingList });
  };

  updateLocalStorage = () => {
    const { recipes, shoppingList, ownedIngredients } = this.state;

    localStorage.setItem('recipes', JSON.stringify(recipes));
    localStorage.setItem('shoppingList', JSON.stringify(shoppingList));
    localStorage.setItem('ownedIngredients', JSON.stringify(ownedIngredients));
  };

  componentDidMount() {
    this.updateShoppingList();
  }

  clearShoppingList = () => {
    this.setState({ shoppingList: {} }, () => {
      localStorage.removeItem('shoppingList');
    });
  };

  render() {
    const { currentRecipe, shoppingList, recipes, ownedIngredients, newOwnedIngredient } = this.state;

    return (
      <div>
        <h1>Recipe App</h1>
        <div>
          <input type="text" value={currentRecipe} onChange={this.handleRecipeChange} />
          <button onClick={this.addRecipe}>Add Recipe</button>
        </div>
        <div>
          <h2>Shopping List</h2>
          {Object.keys(shoppingList).length === 0 ? (
            <p>No items in the shopping list</p>
          ) : (
            <div>
              <ul>
                {Object.entries(shoppingList).map(([ingredient, quantity], index) => (
                  <li key={index}>
                    {quantity} {ingredient}
                  </li>
                ))}
              </ul>
              <button onClick={this.clearShoppingList}>Clear Shopping List</button>
            </div>
          )}
        </div>
        <div>
          <h2>Owned Ingredients</h2>
          <div>
            <input type="text" value={newOwnedIngredient} onChange={this.handleOwnedIngredientChange} />
            <button onClick={this.addOwnedIngredient}>Add Owned Ingredient</button>
          </div>
          {Object.keys(ownedIngredients).length === 0 ? (
            <p>No owned ingredients</p>
          ) : (
            <ul>
              {Object.keys(ownedIngredients).map((ingredient, index) => (
                <li key={index}>{ingredient}</li>
              ))}
            </ul>
          )}
        </div>
        <div className="recipe-container">
          <div className="stored-recipes">
            <h2>Stored Recipes</h2>
            {Object.keys(recipes).length === 0 ? (
              <p>No recipes stored</p>
            ) : (
              <div>
                {Object.keys(recipes).map((recipeName, index) => (
                  <div key={index} className="recipe-box">
                    {recipeName}
                  </div>
                ))}
              </div>
            )}
          </div>
        </div>
      </div>
    );
  }
}

export default RecipeApp;
