import React, { useState } from 'react';
import './App.css';

function App() {
  const [text, setText] = useState('');
  const [outputText, setOutputText] = useState('');
  const [selectedWord, setSelectedWord] = useState('');

  const handleInputChange = (event) => {
    const inputValue = event.target.value;
    setText(inputValue);
    setOutputText(formatText(inputValue))
  };

  const handleWordClick = (word) => {

    setSelectedWord(word+' + some extra information on word');
  }

  const formatText = (input) => {
    const words = input.split(' ');
    const formattedWords = words.map((word, index) => {
      if (word.startsWith('#')) {
        handleWordClick(word)
        return <span key={index}
                     className="highlightedWord"
                     style={{cursor: 'pointer', marginRight: '5px' }}
                    >
                      {word}&nbsp;
                    </span>;
      }
      return word + ' ';
    });
    return formattedWords;
  };

  return (
    <div className="container">
      <input type="text" value={text} onChange={handleInputChange} className="inputBox" />
      <div className="outputBox">{outputText}</div>
      <div className="outputBox">{selectedWord}</div>
    </div>
  );
}

export default App;
