import React from 'react';

class RecipeApp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      recipes: {
        Pancakes: ['Flour', 'Milk', 'Eggs', 'Butter', 'Salt', 'Sugar'],
        Salad: ['Lettuce', 'Tomato', 'Cucumber', 'Onion', 'Olive Oil', 'Vinegar', 'Salt', 'Pepper'],
      },
      currentRecipe: '',
      shoppingList: [],
    };
  }

  handleRecipeChange = (event) => {
    this.setState({ currentRecipe: event.target.value });
  };

  addRecipe = () => {
    const { recipes, currentRecipe } = this.state;

    if (currentRecipe.trim() === '') {
      return;
    }

    const recipeIngredients = recipes[currentRecipe];
    if (!recipeIngredients) {
      alert('Recipe not found!');
      return;
    }

    const shoppingList = [...this.state.shoppingList, ...recipeIngredients];

    this.setState({
      currentRecipe: '',
      shoppingList,
    });
  };

  render() {
    const { currentRecipe, shoppingList } = this.state;

    return (
      <div>
        <h1>Recipe App</h1>
        <div>
          <input type="text" value={currentRecipe} onChange={this.handleRecipeChange} />
          <button onClick={this.addRecipe}>Add Recipe</button>
        </div>
        <div>
          <h2>Shopping List</h2>
          {shoppingList.length === 0 ? (
            <p>No items in the shopping list</p>
          ) : (
            <ul>
              {shoppingList.map((item, index) => (
                <li key={index}>{item}</li>
              ))}
            </ul>
          )}
        </div>
      </div>
    );
  }
}

export default RecipeApp;
