    });
  };


  return (
    <div className="container">
      <input type="text" value={text} onChange={handleInputChange} className="inputBox" />
      <div className="outputBox">{outputText}</div>
      <div className='clickedWordBox'>{clickedWord}</div>
    </div>
  );
}

export default App;
