import React from 'react';
import './App.css';
import FuzzyMatch from 'fuzzy-match';

class RecipeApp extends React.Component {
  constructor(props) {
    super(props);
    const storedRecipes = localStorage.getItem('recipes');
    const storedShoppingList = localStorage.getItem('shoppingList');
    const storedOwnedIngredients = localStorage.getItem('ownedIngredients');

    this.state = {
      recipes: storedRecipes ? JSON.parse(storedRecipes) : {
        Pancakes: ['1 cup Flour', '1 cup Milk', '2 Eggs', '2 tbsp Butter', '1/4 tsp Salt', '1 tbsp Sugar'],
        Salad: ['1 head Lettuce', '2 Tomatoes', '1 Cucumber', '1/2 Onion', '2 tbsp Olive Oil', '1 tbsp Vinegar', 'Salt', 'Pepper'],
      },
      currentRecipe: '',
      shoppingList: storedShoppingList ? JSON.parse(storedShoppingList) : {},
      ownedIngredients: storedOwnedIngredients ? JSON.parse(storedOwnedIngredients) : {},
    };
  }

  findBestMatch = (ingredientName, recipeIngredients) => {
    const matcher = new FuzzyMatch(recipeIngredients);
    const bestMatch = matcher.find(ingredientName);
    return bestMatch && bestMatch.value;
  };

  componentDidUpdate() {
    localStorage.setItem('recipes', JSON.stringify(this.state.recipes));
    localStorage.setItem('shoppingList', JSON.stringify(this.state.shoppingList));
    localStorage.setItem('ownedIngredients', JSON.stringify(this.state.ownedIngredients));
  }

  handleRecipeChange = (event) => {
    this.setState({ currentRecipe: event.target.value });
  };

  addOwnedIngredient = () => {
    const ingredient = window.prompt('Enter the ingredient:');
    const quantity = window.prompt('Enter the quantity:');

    if (ingredient && quantity) {
      const { recipes } = this.state;
      const recipeIngredients = Object.values(recipes).flat();

      const fuzzyMatcher = new FuzzyMatch(recipeIngredients);
      const matchedIngredient = fuzzyMatcher.get(ingredient);

      if (matchedIngredient) {
        const updatedOwnedIngredients = {
          ...this.state.ownedIngredients,
          [matchedIngredient]: quantity,
        };

        this.setState({ ownedIngredients: updatedOwnedIngredients }, () => {
          this.updateShoppingList(Object.values(recipes).flat());
        });
      } else {
        const confirmAddIngredient = window.confirm('Ingredient not found. Do you want to add it?');
        if (confirmAddIngredient) {
          const updatedOwnedIngredients = {
            ...this.state.ownedIngredients,
            [ingredient]: quantity,
          };

          this.setState({ ownedIngredients: updatedOwnedIngredients }, () => {
            this.updateShoppingList(Object.values(recipes).flat());
          });
        }
      }
    }
  };

  addRecipe = () => {
    const { recipes, currentRecipe } = this.state;

    if (currentRecipe.trim() === '') {
      return;
    }

    const recipeIngredients = recipes[currentRecipe];
    if (recipeIngredients) {
      this.updateShoppingList(recipeIngredients);
    } else {
      const confirmAddRecipe = window.confirm('Recipe not found. Do you want to add it?');
      if (confirmAddRecipe) {
        this.addNewRecipe(currentRecipe);
      }
    }
  };

  updateShoppingList = (ingredients) => {
    const { ownedIngredients } = this.state;
    const updatedShoppingList = { ...this.state.shoppingList };

    ingredients.forEach((ingredient) => {
      const spaceIndex = ingredient.indexOf(' ');
      const ingredientName = spaceIndex === -1 ? ingredient : ingredient.slice(spaceIndex + 1);
      const ownedQuantity = ownedIngredients[ingredientName] || 0;

      if (ownedQuantity >= 0 && updatedShoppingList[ingredient]) {
        updatedShoppingList[ingredient] -= ownedQuantity;
        if (updatedShoppingList[ingredient] <= 0) {
          delete updatedShoppingList[ingredient];
        }
      } else if (!updatedShoppingList[ingredient]) {
        updatedShoppingList[ingredient] = 1;
      }
    });

    this.setState({ shoppingList: updatedShoppingList });
  };

  addNewRecipe = (recipeName) => {
    const { recipes } = this.state;
    const ingredients = window.prompt('Enter the ingredients (separated by commas):');
    const recipeIngredients = ingredients.split(',').map((ingredient) => ingredient.trim());

    const updatedRecipes = {
      ...recipes,
      [recipeName]: recipeIngredients,
    };

    this.setState({ recipes: updatedRecipes }, () => {
      this.updateShoppingList(recipeIngredients);
    });
  };

  render() {
    const { recipes, currentRecipe, shoppingList, ownedIngredients } = this.state;
    const recipeOptions = Object.keys(recipes).map((recipe) => (
      <option key={recipe} value={recipe}>
        {recipe}
      </option>
    ));

    const shoppingListItems = Object.entries(shoppingList).map(([ingredient, quantity]) => (
      <li key={ingredient}>
        {`${ingredient} - ${quantity}`}
      </li>
    ));

    const ownedIngredientItems = Object.entries(ownedIngredients).map(([ingredient, quantity]) => (
      <li key={ingredient}>
        {`${ingredient} - ${quantity}`}
      </li>
    ));

    return (
      <div className="App">
        <h1>Recipe App</h1>
        <div>
          <h2>Add Recipe</h2>
          <input type="text" value={currentRecipe} onChange={this.handleRecipeChange} />
          <button onClick={this.addRecipe}>Add</button>
        </div>
        <div>
          <h2>Add Owned Ingredient</h2>
          <button onClick={this.addOwnedIngredient}>Add Ingredient</button>
        </div>
        <div>
          <h2>Recipes</h2>
          <ul>
            {recipeOptions}
          </ul>
        </div>
        <div>
          <h2>Shopping List</h2>
          <ul>
            {shoppingListItems}
          </ul>
        </div>
        <div>
          <h2>Owned Ingredients</h2>
          <ul>
            {ownedIngredientItems}
          </ul>
        </div>
      </div>
    );
  }
}

export default RecipeApp;
