import React from 'react';

class RecipeApp extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      recipes: {},
      currentRecipe: '',
      shoppingList: [],
    };
  }

  handleRecipeChange = (event) => {
    this.setState({ currentRecipe: event.target.value });
  };

  addRecipe = () => {
    const { recipes, currentRecipe } = this.state;

    if (currentRecipe.trim() === '') {
      return;
    }

    const recipeIngredients = currentRecipe.split(',').map((ingredient) => ingredient.trim());
    const updatedRecipes = { ...recipes, [currentRecipe]: recipeIngredients };
    const shoppingList = this.generateShoppingList(updatedRecipes);

    this.setState({
      recipes: updatedRecipes,
      currentRecipe: '',
      shoppingList,
    });
  };

  generateShoppingList = (recipes) => {
    const allIngredients = Object.values(recipes).flat();
    return Array.from(new Set(allIngredients));
  };

  render() {
    const { currentRecipe, shoppingList } = this.state;

    return (
      <div>
        <h1>Recipe App</h1>
        <div>
          <input type="text" value={currentRecipe} onChange={this.handleRecipeChange} />
          <button onClick={this.addRecipe}>Add Recipe</button>
        </div>
        <div>
          <h2>Shopping List</h2>
          {shoppingList.length === 0 ? (
            <p>No items in the shopping list</p>
          ) : (
            <ul>
              {shoppingList.map((item, index) => (
                <li key={index}>{item}</li>
              ))}
            </ul>
          )}
        </div>
      </div>
    );
  }
}

export default App;
