import React from 'react';
import './App.css';

class RecipeApp extends React.Component {
  constructor(props) {
    super(props);
    const storedRecipes = localStorage.getItem('recipes');
    const storedShoppingList = localStorage.getItem('shoppingList');
    const storedOwnedIngredients = localStorage.getItem('ownedIngredients');
    const storedUsedIngredients = localStorage.getItem('usedIngredients');

    this.state = {
      recipes: storedRecipes ? JSON.parse(storedRecipes) : {
        Pancakes: ['1 cup Flour', '1 cup Milk', '2 Eggs', '2 tbsp Butter', '1/4 tsp Salt', '1 tbsp Sugar'],
        Salad: ['1 head Lettuce', '2 Tomatoes', '1 Cucumber', '1/2 Onion', '2 tbsp Olive Oil', '1 tbsp Vinegar', 'Salt', 'Pepper'],
      },
      currentRecipe: '',
      shoppingList: storedShoppingList ? JSON.parse(storedShoppingList) : {},
      ownedIngredients: storedOwnedIngredients ? JSON.parse(storedOwnedIngredients) : {},
      usedIngredients: storedUsedIngredients ? JSON.parse(storedUsedIngredients) : {},
    };
  }

  componentDidUpdate() {
    this.updateLocalStorage();
  }

  updateLocalStorage = () => {
    const { recipes, shoppingList, ownedIngredients, usedIngredients } = this.state;
    localStorage.setItem('recipes', JSON.stringify(recipes));
    localStorage.setItem('shoppingList', JSON.stringify(shoppingList));
    localStorage.setItem('ownedIngredients', JSON.stringify(ownedIngredients));
    localStorage.setItem('usedIngredients', JSON.stringify(usedIngredients));
  };

  handleRecipeChange = (event) => {
    this.setState({ currentRecipe: event.target.value });
  };

  addRecipe = () => {
    const { recipes, currentRecipe } = this.state;

    if (currentRecipe.trim() === '') {
      return;
    }

    const trimmedRecipeName = currentRecipe.trim();
    const recipeIngredients = [...recipes[trimmedRecipeName]];

    if (recipeIngredients) {
      this.updateShoppingList(recipeIngredients);
      this.addUsedIngredients(recipeIngredients);
    } else {
      const confirmAddRecipe = window.confirm('Recipe not found. Do you want to add it?');
      if (confirmAddRecipe) {
        this.addNewRecipe(trimmedRecipeName);
      }
    }
  };

  updateShoppingList = (ingredients) => {
    const { ownedIngredients, usedIngredients } = this.state;
    const updatedShoppingList = { ...this.state.shoppingList };

    ingredients.forEach((ingredient) => {
      const spaceIndex = ingredient.indexOf(' ');
      let quantity;
      let ingredientName;

      if (spaceIndex === -1) {
        quantity = '1';
        ingredientName = ingredient;
      } else {
        quantity = ingredient.slice(0, spaceIndex);
        ingredientName = ingredient.slice(spaceIndex + 1);
      }

      const parsedQuantity = this.parseQuantity(quantity);
      const ownedQuantity = ownedIngredients[ingredientName] ? ownedIngredients[ingredientName].quantity : 0;
      const usedQuantity = usedIngredients[ingredientName] ? usedIngredients[ingredientName] : 0;

      const requiredQuantity = Math.max(parsedQuantity - ownedQuantity - usedQuantity, 0);

      if (updatedShoppingList[ingredientName]) {
        updatedShoppingList[ingredientName] += requiredQuantity;
      } else {
        updatedShoppingList[ingredientName] = requiredQuantity;
      }
    });

    this.setState(
      {
        currentRecipe: '',
        shoppingList: updatedShoppingList,
      },
      () => {
        this.updateLocalStorage();
      }
    );
  };

  clearShoppingList = () => {
    this.setState({ shoppingList: {} }, () => {
      this.updateLocalStorage();
    });
  };

  parseQuantity = (quantity) => {
    if (quantity.trim() === '') {
      return 1; // Default to 1 if no quantity is provided
    }

    if (quantity.includes('/')) {
      const [numerator, denominator] = quantity.split('/');
      return parseFloat(numerator) / parseFloat(denominator);
    }

    return parseFloat(quantity);
  };

  handleOwnedIngredientChange = (ingredientName, event) => {
    const ownedQuantity = parseFloat(event.target.value);
    const { ownedIngredients } = this.state;

    if (ownedQuantity > 0) {
      ownedIngredients[ingredientName] = { quantity: ownedQuantity };
    } else {
      delete ownedIngredients[ingredientName];
    }

    this.setState(
      {
        ownedIngredients: { ...ownedIngredients },
      },
      () => {
        this.updateShoppingList(Object.keys(this.state.recipes).flatMap((recipe) => this.state.recipes[recipe]));
      }
    );
  };

  render() {
    const { currentRecipe, shoppingList, recipes, ownedIngredients } = this.state;

    return (
      <div>
        <h1>Recipe App</h1>
        <div>
          <input type="text" value={currentRecipe} onChange={this.handleRecipeChange} />
          <button onClick={this.addRecipe}>Add Recipe</button>
        </div>
        <div>
          <h2>Shopping List</h2>
          {Object.keys(shoppingList).length === 0 ? (
            <p>No items in the shopping list</p>
          ) : (
            <div>
              <ul>
                {Object.entries(shoppingList).map(([ingredient, quantity], index) => (
                  <li key={index}>
                    {quantity} {ingredient}
                  </li>
                ))}
              </ul>
              <button onClick={this.clearShoppingList}>Clear Shopping List</button>
            </div>
          )}
        </div>
        <div>
          <h2>Saved Recipes</h2>
          {Object.keys(recipes).length === 0 ? (
            <p>No recipes stored</p>
          ) : (
            <div>
              {Object.keys(recipes).map((recipeName, index) => (
                <button key={index} onClick={() => this.addRecipe(recipeName)}>
                  {recipeName}
                </button>
              ))}
            </div>
          )}
        </div>
        <div>
          <h2>Owned Ingredients</h2>
          {Object.keys(recipes).length === 0 ? (
            <p>No recipes stored</p>
          ) : (
            <div>
              {Object.keys(recipes[currentRecipe] || {}).map((ingredient, index) => (
                <div key={index}>
                  <input
                    type="number"
                    min="0"
                    value={ownedIngredients[ingredient] ? ownedIngredients[ingredient].quantity : ''}
                    onChange={(event) => this.handleOwnedIngredientChange(ingredient, event)}
                  />
                  <span>{ingredient}</span>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>
    );
  }

}

export default RecipeApp;
